<script type="text/javascript">
	var newPage = 2;
	var click_page_flag = 1
    var pageTotal = 0;
	$(function(){
        <!--调用Luara示例-->
        $(".photo-slide").luara({width:"500",height:"280",interval:5000,selected:"seleted",deriction:"top"});

    });
    function getShow(obj) {
        //console.log($(obj).parents('div.topnews').find('div.blogs').size());
        var getAjaxUrl = 'https://www.yezismile.com/index/more';
        //console.log(newPage);
        var data = {page:newPage};
        if (click_page_flag == 0) {return false;}
 	 	click_page_flag = 0;
		$.ajax({
            url : getAjaxUrl,
            type: 'POST',
            dataType:'json',
            data :data,
            success:function (msg) {
                if (msg.html != '') {
                    pageTotal++;
                    var getObj  = $(obj).parents('div.topnews').find('div.blogs');
                    var getNum  = parseInt(getObj.length)-1;
                    var target = getObj.eq(getNum);
                    target.after(msg.data);
                    if (msg.count < 5) {
                        $('#alreadyread').show();
                        $(obj).parent().hide();//隐藏加载更多
                    }
                }else{
                    $(obj).parent().hide();//隐藏加载更多
                    $('#alreadyread').show();//显示加载完成
                }
				click_page_flag = 1;
				newPage++;

          }});
    };

    function getInfo(p,t,o){
        var ajax_url = 'https://www.yezismile.com/index/more'; //ajax 请求地址
        var ajax_json = {'page':p,'type':t}; //ajax请求json参数
        var parent_div = o.parent().parent();
        //发送请求
        $.ajax({
            type:'get',
            dataType:'json',
            data:ajax_json,
            url:ajax_url,
            success:function(msg){
                if (msg.html != '') {
                    pageTotal++;
                    var getObj  = $(obj).parents('div.topnews').find('div.blogs');
                    var getNum  = parseInt(getObj.length)-1;
                    var target = getObj.eq(getNum);
                    target.after(msg.data);
                    if (msg.count < 5) {
                        $('#alreadyread').show();
                        $(obj).parent().hide();//隐藏加载更多
                    }
                }else{
                    $(obj).parent().hide();//隐藏加载更多
                    $('#alreadyread').show();//显示加载完成
                }
                click_page_flag = 1;
                newPage++;
            }
        });
    };

</script>

